func TestEstimatorCommon(t *testing.T) {
	for _, test := range []struct {
		code      string
		script    string
		catalogue *Catalogue
		cost      int64
	}{
		{`false`, "AweHXCN1", NewCatalogueV3(), 1},
		{`unit == Unit()`, "AwkAAAAAAAACBQAAAAR1bml0CQEAAAAEVW5pdAAAAACd7sMa", NewCatalogueV3(), 3},
		{`12345 == 12345`, "AwkAAAAAAAACAAAAAAAAADA5AAAAAAAAADA5+DindQ==", NewCatalogueV3(), 3},
		{`let x = 2 * 2; x == 4`, "AwQAAAABeAkAAGgAAAACAAAAAAAAAAACAAAAAAAAAAACCQAAAAAAAAIFAAAAAXgAAAAAAAAAAARdrwMC", NewCatalogueV3(), 12},
		{`let a = "A"; let b = "B"; a + b == "AB"`, "AwQAAAABYQIAAAABQQQAAAABYgIAAAABQgkAAAAAAAACCQABLAAAAAIFAAAAAWEFAAAAAWICAAAAAkFC8C4jQA==", NewCatalogueV3(), 28},
		{`fromBase58String("") == base16'cafebebe'`, "AwkAAAAAAAACCQACWQAAAAECAAAAAAEAAAAEyv6+vpLxJHA=", NewCatalogueV3(), 13},
		{`Address(base58'11111111111111111') == Address(base58'11111111111111111')`, "AwkAAAAAAAACCQEAAAAHQWRkcmVzcwAAAAEBAAAAEQAAAAAAAAAAAAAAAAAAAAAACQEAAAAHQWRkcmVzcwAAAAEBAAAAEQAAAAAAAAAAAAAAAAAAAAAA2+A0og==", NewCatalogueV3(), 5},
		{`toString(Address(base58'3P3336rNSSU8bDAqDb6S5jNs8DJb2bfNmpg')) == "3P3336rNSSU8bDAqDb6S5jNs8DJb2bfNmpf"`, "AwkAAAAAAAACCQAEJQAAAAEJAQAAAAdBZGRyZXNzAAAAAQEAAAAaAVcMIZxOsk2Gw5Avd0ztqi+phtb1Bb83MiUCAAAAIzNQMzMzNnJOU1NVOGJEQXFEYjZTNWpOczhESmIyYmZObXBmb/6mcg==", NewCatalogueV3(), 14},
		{`tx.sender == Address(base58'11111111111111111')`, "AwkAAAAAAAACCAUAAAACdHgAAAAGc2VuZGVyCQEAAAAHQWRkcmVzcwAAAAEBAAAAEQAAAAAAAAAAAAAAAAAAAAAAWc7d/w==", NewCatalogueV3(), 7},
		{`parseIntValue("012345") == 12345`, "AwkAAAAAAAACCQEAAAANcGFyc2VJbnRWYWx1ZQAAAAECAAAABjAxMjM0NQAAAAAAAAAwOXCRV0U=", NewCatalogueV3(), 23},
		{`let x = parseIntValue("12345"); x + x == 0`, "AwQAAAABeAkBAAAADXBhcnNlSW50VmFsdWUAAAABAgAAAAUxMjM0NQkAAAAAAAACCQAAZAAAAAIFAAAAAXgFAAAAAXgAAAAAAAAAAADVoBKt", NewCatalogueV3(), 33},
		{`let x = parseIntValue("12345"); 0 == 0`, "AwQAAAABeAkBAAAADXBhcnNlSW50VmFsdWUAAAABAgAAAAUxMjM0NQkAAAAAAAACAAAAAAAAAAAAAAAAAAAAAAAAk6EsIQ==", NewCatalogueV3(), 8},
		{`let x = parseIntValue("123"); let y = parseIntValue("456");  x + y == y + x`, "AwQAAAABeAkBAAAADXBhcnNlSW50VmFsdWUAAAABAgAAAAMxMjMEAAAAAXkJAQAAAA1wYXJzZUludFZhbHVlAAAAAQIAAAADNDU2CQAAAAAAAAIJAABkAAAAAgUAAAABeAUAAAABeQkAAGQAAAACBQAAAAF5BQAAAAF4sUY0sQ==", NewCatalogueV3(), 63},
		{`let d = [DataEntry("integer", 100500), DataEntry("boolean", true), DataEntry("binary", base16'68656c6c6f'), DataEntry("string", "world")]; getInteger(d, "integer") == 100500`, "", NewCatalogueV3(), 46},
		{`let d = [DataEntry("integer",100500), DataEntry("boolean", true), DataEntry("binary", base16'68656c6c6f'), DataEntry("string", "world")]; getString(d, "string") == "world"`, "", NewCatalogueV3(), 46},
		{`let x = 1 + 2; x == 0`, "AwQAAAABeAkAAGQAAAACAAAAAAAAAAABAAAAAAAAAAACCQAAAAAAAAIFAAAAAXgAAAAAAAAAAABuZPgv", NewCatalogueV3(), 12},
		{`let x = 2 + 2; let y = x - x; x - y == x`, "AwQAAAABeAkAAGQAAAACAAAAAAAAAAACAAAAAAAAAAACBAAAAAF5CQAAZQAAAAIFAAAAAXgFAAAAAXgJAAAAAAAAAgkAAGUAAAACBQAAAAF4BQAAAAF5BQAAAAF4G74APQ==", NewCatalogueV3(), 26},
		{`let a = 1 + 2; let b = 2; let c = a + b; b == 0`, "AwQAAAABYQkAAGQAAAACAAAAAAAAAAABAAAAAAAAAAACBAAAAAFiAAAAAAAAAAACBAAAAAFjCQAAZAAAAAIFAAAAAWEFAAAAAWIJAAAAAAAAAgUAAAABYgAAAAAAAAAAAGbVbuk=", NewCatalogueV3(), 20},
		{`let a = 1 + 2 + 3; let b = 4 + 5; let c = if false then a else b; c == 0`, "AwQAAAABYQkAAGQAAAACCQAAZAAAAAIAAAAAAAAAAAEAAAAAAAAAAAIAAAAAAAAAAAMEAAAAAWIJAABkAAAAAgAAAAAAAAAABAAAAAAAAAAABQQAAAABYwMHBQAAAAFhBQAAAAFiCQAAAAAAAAIFAAAAAWMAAAAAAAAAAABW2XVO", NewCatalogueV3(), 28},
		{`big script`, "", NewCatalogueV2(), 1970},
		{`casino.ride`, "", NewCatalogueV2(), 1760},
		{`XmasTree.ride`, "", NewCatalogueV2(), 1368},
		{`guess.ride`, "", NewCatalogueV2(), 237},
		{`Multisig.ride`, "", NewCatalogueV2(), 388},
		{`AuthorizedTrader.ride`, "", NewCatalogueV2(), 254},
		{`let a = unit; let b = unit; let c = unit; let d = unit; let x = if true then a else b; let y = if false then c else d; x == y`, "AwQAAAABYQUAAAAEdW5pdAQAAAABYgUAAAAEdW5pdAQAAAABYwUAAAAEdW5pdAQAAAABZAUAAAAEdW5pdAQAAAABeAMGBQAAAAFhBQAAAAFiBAAAAAF5AwcFAAAAAWMFAAAAAWQJAAAAAAAAAgUAAAABeAUAAAABeei/I5Y=", NewCatalogueV3(), 47},
		{`match tx {case dt: DataTransaction => !isDefined(getInteger(dt.data, "xxx")) case _ => false }`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAD0RhdGFUcmFuc2FjdGlvbgQAAAACZHQFAAAAByRtYXRjaDAJAQAAAAEhAAAAAQkBAAAACWlzRGVmaW5lZAAAAAEJAAQQAAAAAggFAAAAAmR0AAAABGRhdGECAAAAA3h4eAeneNyG", NewCatalogueV2(), 80},
		{`match tx {case dt: DataTransaction => !isDefined(getInteger(dt.data, "xxx")) case _ => false }`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAD0RhdGFUcmFuc2FjdGlvbgQAAAACZHQFAAAAByRtYXRjaDAJAQAAAAEhAAAAAQkBAAAACWlzRGVmaW5lZAAAAAEJAAQQAAAAAggFAAAAAmR0AAAABGRhdGECAAAAA3h4eAeneNyG", NewCatalogueV3(), 36},
		{`let totalMoney = if (isDefined(getInteger(tx.sender, "totalMoney"))) then extract(getInteger(tx.sender, "totalMoney")) else 0; totalMoney != 0`, "AwQAAAAKdG90YWxNb25leQMJAQAAAAlpc0RlZmluZWQAAAABCQAEGgAAAAIIBQAAAAJ0eAAAAAZzZW5kZXICAAAACnRvdGFsTW9uZXkJAQAAAAdleHRyYWN0AAAAAQkABBoAAAACCAUAAAACdHgAAAAGc2VuZGVyAgAAAAp0b3RhbE1vbmV5AAAAAAAAAAAACQEAAAACIT0AAAACBQAAAAp0b3RhbE1vbmV5AAAAAAAAAAAAdjfmag==", NewCatalogueV3(), 234},
		{`let s = size(toString(1000)); s != 0`, "AwQAAAABcwkAATEAAAABCQABpAAAAAEAAAAAAAAAA+gJAQAAAAIhPQAAAAIFAAAAAXMAAAAAAAAAAACmTwkf", NewCatalogueV3(), 12},
		{`let a = "A"; let x = a + if true then {let c = "C"; c} else {let b = "B"; b}; x == "ABC"`, "AwQAAAABYQIAAAABQQQAAAABeAkAASwAAAACBQAAAAFhAwYEAAAAAWMCAAAAAUMFAAAAAWMEAAAAAWICAAAAAUIFAAAAAWIJAAAAAAAAAgUAAAABeAIAAAADQUJDncKWCg==", NewCatalogueV3(), 37},
		{`let a = addressFromString("cafebebedeadbeef"); a == Address(base16'cafebebedeadbeef')`, "AwQAAAABYQkBAAAAEWFkZHJlc3NGcm9tU3RyaW5nAAAAAQIAAAAQY2FmZWJlYmVkZWFkYmVlZgkAAAAAAAACBQAAAAFhCQEAAAAHQWRkcmVzcwAAAAEBAAAACMr+vr7erb7v7Rvb0w==", NewCatalogueV3(), 135},
		{`match tx {case transfer: TransferTransaction => sigVerify(tx.bodyBytes, tx.proofs[0], tx.senderPublicKey)case _ => false}`, "AwQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAE1RyYW5zZmVyVHJhbnNhY3Rpb24EAAAACHRyYW5zZmVyBQAAAAckbWF0Y2gwCQAB9AAAAAMIBQAAAAJ0eAAAAAlib2R5Qnl0ZXMJAAGRAAAAAggFAAAAAnR4AAAABnByb29mcwAAAAAAAAAAAAgFAAAAAnR4AAAAD3NlbmRlclB1YmxpY0tleQeNAjRw", NewCatalogueV3(), 132},
		{`match tx {case IssueTransaction => true case TransferTransaction => false case ReissueTransaction => true case BurnTransaction => false case ExchangeTransaction => true case SetScriptTransaction => false case SetAssetScriptTransaction => true case SponsorFeeTransaction => false case PaymentTransaction => true case GenesisTransaction => false case _ => false}`, "AwQAAAAHJG1hdGNoMAUAAAACdHgEAAAAEElzc3VlVHJhbnNhY3Rpb24FAAAAByRtYXRjaDAGeIskSg==", NewCatalogueV3(), 11},
		{`match (tx) {case t: TransferTransaction => (t.amount - 1) * 2 - 3 - t.fee case _ => 0} == 0`, "AgkAAAAAAAACBAAAAAckbWF0Y2gwBQAAAAJ0eAMJAAABAAAAAgUAAAAHJG1hdGNoMAIAAAATVHJhbnNmZXJUcmFuc2FjdGlvbgQAAAABdAUAAAAHJG1hdGNoMAkAAGUAAAACCQAAZQAAAAIJAABoAAAAAgkAAGUAAAACCAUAAAABdAAAAAZhbW91bnQAAAAAAAAAAAEAAAAAAAAAAAIAAAAAAAAAAAMIBQAAAAF0AAAAA2ZlZQAAAAAAAAAAAAAAAAAAAAAAADdxFIQ=", NewCatalogueV2(), 36},
		{`match tx {case t: TransferTransaction => let x = if true then t.amount - t.fee else t.amount - t.fee - 1; x == 0 case _ => false}`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAE1RyYW5zZmVyVHJhbnNhY3Rpb24EAAAAAXQFAAAAByRtYXRjaDAEAAAAAXgDBgkAAGUAAAACCAUAAAABdAAAAAZhbW91bnQIBQAAAAF0AAAAA2ZlZQkAAGUAAAACCQAAZQAAAAIIBQAAAAF0AAAABmFtb3VudAgFAAAAAXQAAAADZmVlAAAAAAAAAAABCQAAAAAAAAIFAAAAAXgAAAAAAAAAAAAHiepzew==", NewCatalogueV2(), 41},
		{`let x = 0; let y = if true then x else x + 1; y == 0`, "AgQAAAABeAAAAAAAAAAAAAQAAAABeQMGBQAAAAF4CQAAZAAAAAIFAAAAAXgAAAAAAAAAAAEJAAAAAAAAAgUAAAABeQAAAAAAAAAAALitwEo=", NewCatalogueV2(), 21},
		{`match tx {case tx: TransferTransaction => isDefined(tx.feeAssetId) case _ => false}`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAE1RyYW5zZmVyVHJhbnNhY3Rpb24EAAAAAnR4BQAAAAckbWF0Y2gwCQEAAAAJaXNEZWZpbmVkAAAAAQgFAAAAAnR4AAAACmZlZUFzc2V0SWQHXC5tqw==", NewCatalogueV2(), 58},
		{`match tx {case t: TransferTransaction => isDefined(t.feeAssetId) case _ => false}`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAE1RyYW5zZmVyVHJhbnNhY3Rpb24EAAAAAXQFAAAAByRtYXRjaDAJAQAAAAlpc0RlZmluZWQAAAABCAUAAAABdAAAAApmZWVBc3NldElkB9Agf0U=", NewCatalogueV2(), 58},
		{`match tx {case t: TransferTransaction => isDefined(t.feeAssetId) case _ => false}`, "AgQAAAAHJG1hdGNoMAUAAAACdHgDCQAAAQAAAAIFAAAAByRtYXRjaDACAAAAE1RyYW5zZmVyVHJhbnNhY3Rpb24EAAAAAXQFAAAAByRtYXRjaDAJAQAAAAlpc0RlZmluZWQAAAABCAUAAAABdAAAAApmZWVBc3NldElkB9Agf0U=", NewCatalogueV2(), 58},
		{`let a = addressFromStringValue("3P2USE3iYK5w7jNahAUHTytNbVRccGZwQH3"); let i = getInteger(a, "integer"); let x = match i {case i: Int => i case _ => 0}; x == 100500`, "", NewCatalogueV3(), 268},
		{`func first(a: Int, b: Int) = {let x = a + b; x}; first(1, 2) == 0`, "AwoBAAAABWZpcnN0AAAAAgAAAAFhAAAAAWIEAAAAAXgJAABkAAAAAgUAAAABYQUAAAABYgUAAAABeAkAAAAAAAACCQEAAAAFZmlyc3QAAAACAAAAAAAAAAABAAAAAAAAAAACAAAAAAAAAAAAm+QHtw==", NewCatalogueV3(), 33},
		{`func f(a: Int) = 1; func g(a: Int) = 2; f(g(1)) == 0`, "AwoBAAAAAWYAAAABAAAAAWEAAAAAAAAAAAEKAQAAAAFnAAAAAQAAAAFhAAAAAAAAAAACCQAAAAAAAAIJAQAAAAFmAAAAAQkBAAAAAWcAAAABAAAAAAAAAAABAAAAAAAAAAAAT0GP5g==", NewCatalogueV3(), 25},
		{`func inc(y: Int) = y + 1; let xxx = 5; inc(xxx) == 1`, "AwoBAAAAA2luYwAAAAEAAAABeQkAAGQAAAACBQAAAAF5AAAAAAAAAAABBAAAAAN4eHgAAAAAAAAAAAUJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAA3h4eAAAAAAAAAAAAbumbXA=", NewCatalogueV3(), 25},
		{`func f() = {func f() = {func f() = {1}; f()}; f()}; f() == 0`, "AwoBAAAAAWYAAAAACgEAAAABZgAAAAAKAQAAAAFmAAAAAAAAAAAAAAAAAQkBAAAAAWYAAAAACQEAAAABZgAAAAAJAAAAAAAAAgkBAAAAAWYAAAAAAAAAAAAAAAAAYYLPvQ==", NewCatalogueV3(), 18},
		{`let me = addressFromStringValue(""); func get() = getStringValue(this, ""); get() + get() + get() == ""`, "AwQAAAACbWUJAQAAABxAZXh0clVzZXIoYWRkcmVzc0Zyb21TdHJpbmcpAAAAAQIAAAAACgEAAAADZ2V0AAAAAAkBAAAAEUBleHRyTmF0aXZlKDEwNTMpAAAAAgUAAAAEdGhpcwIAAAAACQAAAAAAAAIJAAEsAAAAAgkAASwAAAACCQEAAAADZ2V0AAAAAAkBAAAAA2dldAAAAAAJAQAAAANnZXQAAAAAAgAAAACvuyuT", NewCatalogueV3(), 341},
	} {
		r, err := reader.NewReaderFromBase64(test.script)
		require.NoError(t, err, test.code)
		script, err := ast.BuildScript(r)
		require.NoError(t, err, test.code)
		e1 := NewEstimator(1, test.catalogue, ast.VariablesV3())
		e2 := NewEstimator(2, test.catalogue, ast.VariablesV3())
		cost1, err := e1.Estimate(script)
		require.NoError(t, err, test.code)
		cost2, err := e2.Estimate(script)
		require.NoError(t, err, test.code)
		assert.Equal(t, test.cost, cost1, test.code)
		assert.Equal(t, test.cost, cost2, test.code)
	}
}

func TestEstimatorFix(t *testing.T) {
	for _, test := range []struct {
		version   int
		code      string
		script    string
		catalogue *Catalogue
		cost      int64
	}{
		{1, `V1: func f(a: Int) = a; f(1) == 1`, "AwoBAAAAAWYAAAABAAAAAWEFAAAAAWEJAAAAAAAAAgkBAAAAAWYAAAABAAAAAAAAAAABAAAAAAAAAAABAYVjTw==", NewCatalogueV3(), 16},
		{2, `V2: func f(a: Int) = a; f(1) == 1`, "AwoBAAAAAWYAAAABAAAAAWEFAAAAAWEJAAAAAAAAAgkBAAAAAWYAAAABAAAAAAAAAAABAAAAAAAAAAABAYVjTw==", NewCatalogueV3(), 16},
		{1, `V1: let me = addressFromStringValue(""); func get() = getStringValue(me, ""); get() + get() == ""`, "AwQAAAACbWUJAQAAABxAZXh0clVzZXIoYWRkcmVzc0Zyb21TdHJpbmcpAAAAAQIAAAAACgEAAAADZ2V0AAAAAAkBAAAAEUBleHRyTmF0aXZlKDEwNTMpAAAAAgUAAAACbWUCAAAAAAkAAAAAAAACCQABLAAAAAIJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAIAAAAAiXGA4g==", NewCatalogueV3(), 478},
		{2, `V2: let me = addressFromStringValue(""); func get() = getStringValue(me, ""); get() + get() == ""`, "AwQAAAACbWUJAQAAABxAZXh0clVzZXIoYWRkcmVzc0Zyb21TdHJpbmcpAAAAAQIAAAAACgEAAAADZ2V0AAAAAAkBAAAAEUBleHRyTmF0aXZlKDEwNTMpAAAAAgUAAAACbWUCAAAAAAkAAAAAAAACCQABLAAAAAIJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAIAAAAAiXGA4g==", NewCatalogueV3(), 353},
		{1, `V1: func inc(xxx: Int) = xxx + 1; let xxx = 5; inc(xxx) == 1`, "AwoBAAAAA2luYwAAAAEAAAADeHh4CQAAZAAAAAIFAAAAA3h4eAAAAAAAAAAAAQQAAAADeHh4AAAAAAAAAAAFCQAAAAAAAAIJAQAAAANpbmMAAAABBQAAAAN4eHgAAAAAAAAAAAFgML5p", NewCatalogueV3(), 25},
		{2, `V2: func inc(xxx: Int) = xxx + 1; let xxx = 5; inc(xxx) == 1`, "AwoBAAAAA2luYwAAAAEAAAADeHh4CQAAZAAAAAIFAAAAA3h4eAAAAAAAAAAAAQQAAAADeHh4AAAAAAAAAAAFCQAAAAAAAAIJAQAAAANpbmMAAAABBQAAAAN4eHgAAAAAAAAAAAFgML5p", NewCatalogueV3(), 25},
		{1, `V1: func inc(y: Int) = y + 1; let xxx = 5; inc(xxx) == 1`, "AwoBAAAAA2luYwAAAAEAAAABeQkAAGQAAAACBQAAAAF5AAAAAAAAAAABBAAAAAN4eHgAAAAAAAAAAAUJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAA3h4eAAAAAAAAAAAAbumbXA=", NewCatalogueV3(), 25},
		{2, `V2: func inc(y: Int) = y + 1; let xxx = 5; inc(xxx) == 1`, "AwoBAAAAA2luYwAAAAEAAAABeQkAAGQAAAACBQAAAAF5AAAAAAAAAAABBAAAAAN4eHgAAAAAAAAAAAUJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAA3h4eAAAAAAAAAAAAbumbXA=", NewCatalogueV3(), 25},
		{1, `V1: func inc(y: Int) = y + 1; inc({let x = 5; x}) == 0`, "AwoBAAAAA2luYwAAAAEAAAABeQkAAGQAAAACBQAAAAF5AAAAAAAAAAABCQAAAAAAAAIJAQAAAANpbmMAAAABBAAAAAF4AAAAAAAAAAAFBQAAAAF4AAAAAAAAAAAADaujqA==", NewCatalogueV3(), 25},
		{2, `V2: func inc(y: Int) = y + 1; inc({let x = 5; x}) == 0`, "AwoBAAAAA2luYwAAAAEAAAABeQkAAGQAAAACBQAAAAF5AAAAAAAAAAABCQAAAAAAAAIJAQAAAANpbmMAAAABBAAAAAF4AAAAAAAAAAAFBQAAAAF4AAAAAAAAAAAADaujqA==", NewCatalogueV3(), 25},
		{1, `V1: func add(x: Int, y: Int) = x + y; let a = 2; let b = 3; add(a, b) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAWEAAAAAAAAAAAIEAAAAAWIAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAWEFAAAAAWIAAAAAAAAAAAXSOexF", NewCatalogueV3(), 40},
		{2, `V2: func add(x: Int, y: Int) = x + y; let a = 2; let b = 3; add(a, b) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAWEAAAAAAAAAAAIEAAAAAWIAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAWEFAAAAAWIAAAAAAAAAAAXSOexF", NewCatalogueV3(), 40},
		{1, `V1: func add(x: Int, y: Int) = x + y; let a = 2; let y = 3; add(a, y) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAWEAAAAAAAAAAAIEAAAAAXkAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAWEFAAAAAXkAAAAAAAAAAAVtyJg5", NewCatalogueV3(), 40},
		{2, `V2: func add(x: Int, y: Int) = x + y; let a = 2; let y = 3; add(a, y) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAWEAAAAAAAAAAAIEAAAAAXkAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAWEFAAAAAXkAAAAAAAAAAAVtyJg5", NewCatalogueV3(), 40},
		{1, `V1: func add(x: Int, y: Int) = x + y; let x = 2; let y = 3; add(x, y) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAXgAAAAAAAAAAAIEAAAAAXkAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAXgFAAAAAXkAAAAAAAAAAAVMfO15", NewCatalogueV3(), 40},
		{2, `V2: func add(x: Int, y: Int) = x + y; let x = 2; let y = 3; add(x, y) == 5`, "AwoBAAAAA2FkZAAAAAIAAAABeAAAAAF5CQAAZAAAAAIFAAAAAXgFAAAAAXkEAAAAAXgAAAAAAAAAAAIEAAAAAXkAAAAAAAAAAAMJAAAAAAAAAgkBAAAAA2FkZAAAAAIFAAAAAXgFAAAAAXkAAAAAAAAAAAVMfO15", NewCatalogueV3(), 40},
		{1, `V1: let f124 = addressFromStringValue(""); func get() = getStringValue(f124, ""); get() + get() + get() == ""`, "AwQAAAAEZjEyNAkBAAAAHEBleHRyVXNlcihhZGRyZXNzRnJvbVN0cmluZykAAAABAgAAAAAKAQAAAANnZXQAAAAACQEAAAARQGV4dHJOYXRpdmUoMTA1MykAAAACBQAAAARmMTI0AgAAAAAJAAAAAAAAAgkAASwAAAACCQABLAAAAAIJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAkBAAAAA2dldAAAAAACAAAAAOS3o9c=", NewCatalogueV3(), 716},
		{2, `V2: let f124 = addressFromStringValue(""); func get() = getStringValue(f124, ""); get() + get() + get() == ""`, "AwQAAAAEZjEyNAkBAAAAHEBleHRyVXNlcihhZGRyZXNzRnJvbVN0cmluZykAAAABAgAAAAAKAQAAAANnZXQAAAAACQEAAAARQGV4dHJOYXRpdmUoMTA1MykAAAACBQAAAARmMTI0AgAAAAAJAAAAAAAAAgkAASwAAAACCQABLAAAAAIJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAkBAAAAA2dldAAAAAACAAAAAOS3o9c=", NewCatalogueV3(), 466},
		{1, `V1: let f1 = Address(base58''); func get() = getStringValue(f1, ""); get() + get() + get() == ""`, "AwQAAAACZjEJAQAAAAdBZGRyZXNzAAAAAQEAAAAACgEAAAADZ2V0AAAAAAkBAAAAEUBleHRyTmF0aXZlKDEwNTMpAAAAAgUAAAACZjECAAAAAAkAAAAAAAACCQABLAAAAAIJAAEsAAAAAgkBAAAAA2dldAAAAAAJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAIAAAAA9CXWjw==", NewCatalogueV3(), 347},
		{2, `V2: let f1 = Address(base58''); func get() = getStringValue(f1, ""); get() + get() + get() == ""`, "AwQAAAACZjEJAQAAAAdBZGRyZXNzAAAAAQEAAAAACgEAAAADZ2V0AAAAAAkBAAAAEUBleHRyTmF0aXZlKDEwNTMpAAAAAgUAAAACZjECAAAAAAkAAAAAAAACCQABLAAAAAIJAAEsAAAAAgkBAAAAA2dldAAAAAAJAQAAAANnZXQAAAAACQEAAAADZ2V0AAAAAAIAAAAA9CXWjw==", NewCatalogueV3(), 343},
		{1, `V1: let me = 1 + 1 + 1 + 1; func third(p: Int) = me; func second(me: Int) = third(me); func first() = second(1); first() + first() + first() + first() + first() + first() == 0`, "", NewCatalogueV3(), 165},
		{2, `V2: let me = 1 + 1 + 1 + 1; func third(p: Int) = me; func second(me: Int) = third(me); func first() = second(1); first() + first() + first() + first() + first() + first() == 0`, "", NewCatalogueV3(), 130},
		{2, `V2: let me = 1 + 1 + 1 + 1; func third(p: Int) = me; func second(me: Int) = third(me); func first() = second(1); first() + first() == 0`, "", NewCatalogueV3(), 62},
		{1, `V1: let b = false; let x = if b then {func aaa(i:Int) = i + i + i + i + i + i; aaa(1)} else {func aaa(i: Int) = i + i + i + i; aaa(2)}; x == 6`, "", NewCatalogueV3(), 47},
		{2, `V2: let b = false; let x = if b then {func aaa(i:Int) = i + i + i + i + i + i; aaa(1)} else {func aaa(i: Int) = i + i + i + i; aaa(2)}; x == 6`, "", NewCatalogueV3(), 47},
	} {
		r, err := reader.NewReaderFromBase64(test.script)
		require.NoError(t, err, test.code)
		script, err := ast.BuildScript(r)
		require.NoError(t, err, test.code)
		e := NewEstimator(test.version, test.catalogue, ast.VariablesV3())
		cost, err := e.Estimate(script)
		require.NoError(t, err, test.code)
		assert.Equal(t, test.cost, cost, test.code)
	}
}

func TestDAppEstimation(t *testing.T) {
	for _, test := range []struct {
		version int
		code    string
		script  string
		cost    int64
	}{
		{1, "@Verifier(tx) func verify() = false", "AAIDAAAAAAAAAAIIAQAAAAAAAAAAAAAAAQAAAAJ0eAEAAAAGdmVyaWZ5AAAAAAcysh6J", 11},
		{2, "@Verifier(tx) func verify() = false", "AAIDAAAAAAAAAAIIAQAAAAAAAAAAAAAAAQAAAAJ0eAEAAAAGdmVyaWZ5AAAAAAcysh6J", 11},
		{1, `let a = 1\n@Verifier(tx) func verify() = false`, "AAIDAAAAAAAAAAIIAQAAAAEAAAAAAWEAAAAAAAAAAAEAAAAAAAAAAQAAAAJ0eAEAAAAGdmVyaWZ5AAAAAAdVrdkQ", 16},
		{2, `let a = 1\n@Verifier(tx) func verify() = false`, "AAIDAAAAAAAAAAIIAQAAAAEAAAAAAWEAAAAAAAAAAAEAAAAAAAAAAQAAAAJ0eAEAAAAGdmVyaWZ5AAAAAAdVrdkQ", 16},
		{1, `let a = 1\nfunc inc(v: Int) = {v + 1}\n@Verifier(tx) func verify() = false`, "AAIDAAAAAAAAAAIIAQAAAAIAAAAAAWEAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAHDMc8rg==", 21},
		{2, `let a = 1\nfunc inc(v: Int) = {v + 1}\n@Verifier(tx) func verify() = false`, "AAIDAAAAAAAAAAIIAQAAAAIAAAAAAWEAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAHDMc8rg==", 21},
		{1, `let a = 1\nfunc inc(v: Int) = {v + 1}\n@Verifier(tx) func verify() = inc(a) == 2`, "AAIDAAAAAAAAAAIIAQAAAAIAAAAAAWEAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAAWEAAAAAAAAAAAJtD5WX", 35},
		{2, `let a = 1\nfunc inc(v: Int) = {v + 1}\n@Verifier(tx) func verify() = inc(a) == 2`, "AAIDAAAAAAAAAAIIAQAAAAIAAAAAAWEAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAAWEAAAAAAAAAAAJtD5WX", 35},
		{1, `let a = 1\nlet b = 1\nfunc inc(v: Int) = {v + 1}\nfunc add(x: Int, y: Int) = {x + y}\n@Verifier(tx) func verify() = inc(a) == add(a, b)`, "AAIDAAAAAAAAAAIIAQAAAAQAAAAAAWEAAAAAAAAAAAEAAAAAAWIAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAQAAAANhZGQAAAACAAAAAXgAAAABeQkAAGQAAAACBQAAAAF4BQAAAAF5AAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAAWEJAQAAAANhZGQAAAACBQAAAAFhBQAAAAFiDbIkmw==", 66},
		{2, `let a = 1\nlet b = 1\nfunc inc(v: Int) = {v + 1}\nfunc add(x: Int, y: Int) = {x + y}\n@Verifier(tx) func verify() = inc(a) == add(a, b)`, "AAIDAAAAAAAAAAIIAQAAAAQAAAAAAWEAAAAAAAAAAAEAAAAAAWIAAAAAAAAAAAEBAAAAA2luYwAAAAEAAAABdgkAAGQAAAACBQAAAAF2AAAAAAAAAAABAQAAAANhZGQAAAACAAAAAXgAAAABeQkAAGQAAAACBQAAAAF4BQAAAAF5AAAAAAAAAAEAAAACdHgBAAAABnZlcmlmeQAAAAAJAAAAAAAAAgkBAAAAA2luYwAAAAEFAAAAAWEJAQAAAANhZGQAAAACBQAAAAFhBQAAAAFiDbIkmw==", 66},
		{1, `let a = 1\nlet b = 1\nlet messages = ["INFO", "WARN"]\nfunc inc(v: Int) = {v + 1}\nfunc add(x: Int, y: Int) = {x + y}\nfunc msg(i: Int) = {messages[i]}\n@Verifier(tx) func verify() = if inc(a) == add(a, b) then throw(msg(a)) else throw(msg(b))`, "", 100},
		{2, `let a = 1\nlet b = 1\nlet messages = ["INFO", "WARN"]\nfunc inc(v: Int) = {v + 1}\nfunc add(x: Int, y: Int) = {x + y}\nfunc msg(i: Int) = {messages[i]}\n@Verifier(tx) func verify() = if inc(a) == add(a, b) then throw(msg(a)) else throw(msg(b))`, "", 100},
		{1, `@Callable(i)func f() = {WriteSet([DataEntry("YYY", "XXX")])}`, "AAIDAAAAAAAAAAQIARIAAAAAAAAAAAEAAAABaQEAAAABZgAAAAAJAQAAAAhXcml0ZVNldAAAAAEJAARMAAAAAgkBAAAACURhdGFFbnRyeQAAAAICAAAAA1lZWQIAAAADWFhYBQAAAANuaWwAAAAAeFguLA==", 19},
		{2, `@Callable(i)func f() = {WriteSet([DataEntry("YYY", "XXX")])}`, "AAIDAAAAAAAAAAQIARIAAAAAAAAAAAEAAAABaQEAAAABZgAAAAAJAQAAAAhXcml0ZVNldAAAAAEJAARMAAAAAgkBAAAACURhdGFFbnRyeQAAAAICAAAAA1lZWQIAAAADWFhYBQAAAANuaWwAAAAAeFguLA==", 19},
		{1, `@Callable(i)func f() = {let callerAddress = toBase58String(i.caller.bytes); WriteSet([DataEntry(callerAddress, "XXX")])}`, "AAIDAAAAAAAAAAQIARIAAAAAAAAAAAEAAAABaQEAAAABZgAAAAAEAAAADWNhbGxlckFkZHJlc3MJAAJYAAAAAQgIBQAAAAFpAAAABmNhbGxlcgAAAAVieXRlcwkBAAAACFdyaXRlU2V0AAAAAQkABEwAAAACCQEAAAAJRGF0YUVudHJ5AAAAAgUAAAANY2FsbGVyQWRkcmVzcwIAAAADWFhYBQAAAANuaWwAAAAAe3xtyw==", 42},
		{2, `@Callable(i)func f() = {let callerAddress = toBase58String(i.caller.bytes); WriteSet([DataEntry(callerAddress, "XXX")])}`, "AAIDAAAAAAAAAAQIARIAAAAAAAAAAAEAAAABaQEAAAABZgAAAAAEAAAADWNhbGxlckFkZHJlc3MJAAJYAAAAAQgIBQAAAAFpAAAABmNhbGxlcgAAAAVieXRlcwkBAAAACFdyaXRlU2V0AAAAAQkABEwAAAACCQEAAAAJRGF0YUVudHJ5AAAAAgUAAAANY2FsbGVyQWRkcmVzcwIAAAADWFhYBQAAAANuaWwAAAAAe3xtyw==", 42},

		{1, `let messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int) = {WriteSet([DataEntry("m", msg(x))])}`, "", 57},
		{2, `let messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int) = {WriteSet([DataEntry("m", msg(x))])}`, "", 57},
		{1, `let messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int, y: Int) = {WriteSet([DataEntry("m", msg(x))])}`, "", 63},
		{2, `let messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int, y: Int) = {WriteSet([DataEntry("m", msg(x))])}`, "", 63},
		{1, `let a = 1\nlet messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int) = {let m = msg(x); let callerAddress = toBase58String(i.caller.bytes); WriteSet([DataEntry(callerAddress + "-m", m)])}`, "", 103},
		{2, `let a = 1\nlet messages = ["INFO", "WARN"]\nfunc msg(i: Int) = {messages[i]}\n@Callable(i)func tellme(x: Int) = {let m = msg(x); let callerAddress = toBase58String(i.caller.bytes); WriteSet([DataEntry(callerAddress + "-m", m)])}`, "", 103},

		{1, "8ball", "", 380},
		{2, "8ball", "", 380},
		{1, "bank_dapp", "", 629},
		{2, "bank_dapp", "", 629},
		{1, "fomo", "", 429},
		{2, "fomo", "", 429},
		{1, "momentaryLottery", "", 488},
		{2, "momentaryLottery", "", 488},
		{1, "wallet", "", 216},
		{2, "wallet", "", 216},
	} {
		r, err := reader.NewReaderFromBase64(test.script)
		require.NoError(t, err, fmt.Sprintf("Failure: V%d: %s: %v", test.version, test.code, err))
		s, err := ast.BuildScript(r)
		require.NoError(t, err, fmt.Sprintf("Failure: V%d: %s: %v", test.version, test.code, err))
		require.True(t, s.IsDapp(), fmt.Sprintf("Failure: V%d: %s: not a DApp", test.version, test.code))
		e := NewEstimator(test.version, NewCatalogueV3(), ast.VariablesV3())
		cost, err := e.Estimate(s)
		require.NoError(t, err, fmt.Sprintf("Failure: V%d: %s: %v", test.version, test.code, err))
		assert.Equal(t, test.cost, cost, fmt.Sprintf("Failure: V%d: %s: unexpected cost %d", test.version, test.code, cost))
	}
}
