package serialization

import (
	"encoding/base64"
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
	"github.com/wavesplatform/gowaves/pkg/ride/ast"
)

func TestErrors(t *testing.T) {
	for _, test := range []struct {
		source string
		err    string
	}{
		{"", "failed to parse script: invalid source length -4"},
		{"AQQAAAABaQAAAAAAAAAAAQQAAAABcwIAAAAGc3RyaW5nCQAAAAAAAAIJAAGkAAAAAQUAAAABaQUAAAABcwIsH73=", "invalid script checksum"},
	} {
		src, err := base64.StdEncoding.DecodeString(test.source)
		require.NoError(t, err)
		_, err = Parse(src)
		assert.EqualError(t, err, test.err)
	}
}

func TestParseScripts(t *testing.T) {
	for _, test := range []struct {
		comment string
		content ast.ContentType
		libVer  ast.LibraryVersion
		blockV2 bool
		source  string
		node    ast.Node
	}{
		{`V1: true`, ast.ContentTypeExpression, 1, false, "AQa3b8tH", ast.NewBooleanNode(true)},
		{`V2: true`, ast.ContentTypeExpression, 2, false, "AgZ7TN8j", ast.NewBooleanNode(true)},
		{`V3: true`, ast.ContentTypeExpression, 3, false, "AwZd0cYf", ast.NewBooleanNode(true)},
		{`V1: let i = 1; let s = "string"; toString(i) == s`, ast.ContentTypeExpression, 1, false, "AQQAAAABaQAAAAAAAAAAAQQAAAABcwIAAAAGc3RyaW5nCQAAAAAAAAIJAAGkAAAAAQUAAAABaQUAAAABcwIsH74=",
			ast.NewAssignmentNode("i", ast.NewLongNode(1), ast.NewAssignmentNode("s", ast.NewStringNode("string"), ast.NewFunctionCallNode(ast.NativeFunction("0"), []ast.Node{ast.NewFunctionCallNode(ast.NativeFunction("420"), []ast.Node{ast.NewReferenceNode("i")}), ast.NewReferenceNode("s")})))},
		{`V3: if (true) then {let r = true; r} else {let r = false; r}`, ast.ContentTypeExpression, 3, false, "AwMGBAAAAAFyBgUAAAABcgQAAAABcgcFAAAAAXJ/ok0E",
			ast.NewConditionalNode(ast.NewBooleanNode(true), ast.NewAssignmentNode("r", ast.NewBooleanNode(true), ast.NewReferenceNode("r")), ast.NewAssignmentNode("r", ast.NewBooleanNode(false), ast.NewReferenceNode("r")))},
		{`V3: func abs(i:Int) = if (i >= 0) then i else -i; abs(-10) == 10`, ast.ContentTypeExpression, 3, true, "AwoBAAAAA2FicwAAAAEAAAABaQMJAABnAAAAAgUAAAABaQAAAAAAAAAAAAUAAAABaQkBAAAAAS0AAAABBQAAAAFpCQAAAAAAAAIJAQAAAANhYnMAAAABAP/////////2AAAAAAAAAAAKmp8BWw==",
			ast.NewFunctionDeclarationNode("abs", []string{"i"}, ast.NewConditionalNode(ast.NewFunctionCallNode(ast.NativeFunction("103"), []ast.Node{ast.NewReferenceNode("i"), ast.NewLongNode(0)}), ast.NewReferenceNode("i"), ast.NewFunctionCallNode(ast.UserFunction("-"), []ast.Node{ast.NewReferenceNode("i")})), ast.NewFunctionCallNode(ast.NativeFunction("0"), []ast.Node{ast.NewFunctionCallNode(ast.UserFunction("abs"), []ast.Node{ast.NewLongNode(-10)}), ast.NewLongNode(10)}))},
		{`V3: if (true) then {if (false) then {func XX() = true; XX()} else {func XX() = false; XX()}} else {if (true) then {let x = false; x} else {let x = true; x}}`,
			ast.ContentTypeExpression, 3, true, "AwMGAwcKAQAAAAJYWAAAAAAGCQEAAAACWFgAAAAACgEAAAACWFgAAAAABwkBAAAAAlhYAAAAAAMGBAAAAAF4BwUAAAABeAQAAAABeAYFAAAAAXgYYeMi",
			ast.NewConditionalNode(ast.NewBooleanNode(true),
				ast.NewConditionalNode(ast.NewBooleanNode(false),
					ast.NewFunctionDeclarationNode("XX", []string{}, ast.NewBooleanNode(true), ast.NewFunctionCallNode(ast.UserFunction("XX"), []ast.Node{})),
					ast.NewFunctionDeclarationNode("XX", []string{}, ast.NewBooleanNode(false), ast.NewFunctionCallNode(ast.UserFunction("XX"), []ast.Node{}))),
				ast.NewConditionalNode(ast.NewBooleanNode(true),
					ast.NewAssignmentNode("x", ast.NewBooleanNode(false), ast.NewReferenceNode("x")),
					ast.NewAssignmentNode("x", ast.NewBooleanNode(true), ast.NewReferenceNode("x"))))},
	} {
		src, err := base64.StdEncoding.DecodeString(test.source)
		require.NoError(t, err, test.comment)
		tree, err := Parse(src)
		require.NoError(t, err, test.comment)
		assert.NotNil(t, tree, test.comment)
		assert.Equal(t, test.content, tree.ContentType, test.comment)
		assert.Equal(t, test.libVer, tree.LibVersion, test.comment)
		assert.Equal(t, test.blockV2, tree.HasBlockV2, test.comment)
		assert.Equal(t, 0, len(tree.Declarations))
		assert.Equal(t, 0, len(tree.Functions))
		assert.Equal(t, test.node, tree.Verifier)
	}
}

func TestParseDApps(t *testing.T) {
	for _, test := range []struct {
		comment      string
		content      ast.ContentType
		libVer       ast.LibraryVersion
		declarations int
		functions    int
		verifier     bool
		source       string
	}{
		{`8ball.ride`, ast.ContentTypeApplication, 3, 4, 1, false, ""},
		{`bank_dapp.ride`, ast.ContentTypeApplication, 3, 0, 4, true, ""},
		{`fomo.ride`, ast.ContentTypeApplication, 3, 4, 2, false, ""},
		{`momentaryLottery.ride`, ast.ContentTypeApplication, 3, 2, 2, false, ""},
		{`wallet.ride`, ast.ContentTypeApplication, 3, 0, 2, true, ""},
		{`let messages = ["INFO", "WARN"] func msg(i: Int) = {messages[i]} @Callable(i)func tellme(x: Int, y: Int) = {WriteSet([DataEntry("m", msg(x))])}`, ast.ContentTypeApplication, 3, 2, 1, false, ""},
		{`V6:@Callable(i)func bar() = {let res = invoke(Address(base58'3P8eZVKS7a4troGckytxaefLAi9w7P5aMna'), "foo",[], [AttachedPayment(unit, 500)]); if res == 17 then [] else throw("Bad returned value")}`, ast.ContentTypeApplication, 6, 0, 1, false, "BgIECAISAAABAWkBA2JhcgAEA3JlcwkA/AcECQEHQWRkcmVzcwEBGgFXSbIqC+dSm+dDCCL8KamODy9oLyPQygrLAgNmb28FA25pbAkAzAgCCQEPQXR0YWNoZWRQYXltZW50AgUEdW5pdAD0AwUDbmlsAwkAAAIFA3JlcwARBQNuaWwJAAIBAhJCYWQgcmV0dXJuZWQgdmFsdWUAN8s8Bg=="},
	} {
		src, err := base64.StdEncoding.DecodeString(test.source)
		require.NoError(t, err, test.comment)
		tree, err := Parse(src)
		require.NoError(t, err, test.comment)
		assert.NotNil(t, tree, test.comment)
		assert.Equal(t, test.content, tree.ContentType, test.comment)
		assert.Equal(t, test.libVer, tree.LibVersion, test.comment)
		assert.Equal(t, test.declarations, len(tree.Declarations), test.comment)
		assert.Equal(t, test.functions, len(tree.Functions))
		assert.Equal(t, test.verifier, tree.HasVerifier())
	}
}

func BenchmarkParseScript(b *testing.B) {
	// casino.ride
	code := ""
	src, err := base64.StdEncoding.DecodeString(code)
	if err != nil {
		b.Fail()
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		tree, err := Parse(src)
		if err != nil {
			b.Fail()
		}
		_ = tree
	}
}

